package chapter2;

public class Constant {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// 상수 : 변하지 않는 값 (변수 : 변하는 값)
		// 상수 선언은 final 키워드 사용
		
		final int MAX_NUM = 100;
		final double PI = 3.14;
		// PI = 3.15; 위에 final로 선언을 해서 값 변경 불가로 오류 남
		
 		
		

	}

}
